import { ProgramMetadata } from "@gear-js/api";
import { useState } from "react";
import { useApi, useAlert } from "@gear-js/react-hooks";
import { Button, Card, Center, Heading, VStack, Text, Box } from "@chakra-ui/react";

function ReadState() {
  const { api } = useApi();

  const alert = useAlert();

  const [fullState, setFullState] = useState<any | undefined>(0);
  const [numberState, setNumberState] = useState(0);

  const ColorState=[
    "green.300",
    "red.300"
  ];


    // Add your programID
  const programIDFT =
  "0xbf4536c0a377d93e9be5f222cb4c246f8f7c7ec4155d078cf6fddae3b43dbd1b";

// Add your metadata.txt
const meta =
  "";

  const metadata = ProgramMetadata.from(meta);

  const getState = () => {
    api.programState
      .read({ programId: programIDFT}, metadata)
      .then((result) => {
        setFullState(result.toJSON());
        var value = JSON.stringify(fullState.currentLight);
        if (value === JSON.stringify("Enable")) {
          setNumberState(0);
          return 0;
        } else {
          setNumberState(1);
        }
      })
      .catch(({ message }: Error) => alert.error(message));
  };

  getState();

  return (
    <Box className="circle" w={20} h={20} borderRadius="full" backgroundColor={ColorState[numberState]}></Box>
  );
}

export { ReadState };
